/**
 * Class de gestion des formulaire
 * 
 * @todo : Gere les radio et checkbox
 * @author Nicolas
 * @category check
 */
var Checker = function(){
	
	/**
	 * Stocke la liste des champs a verifier
	 * 
	 * @var array
	 */
	this.liste_check = new Array();
	
	/**
	 * Stocke le message d'erreur
	 * 
	 * @var array
	 */
	this.error_message = new Array();
	
	/**
	 * remet a zero la liste de verif
	 * 
	 * @return void
	 */
	this.reset_checklist = function(){
		this.liste_check = new Array();
	};
	
	/**
	 * Ajoute un champs pour la verif
	 * 
	 * @param string id
	 * @param string options
	 */
	this.add_to_checklist = function(id, options){
		this.liste_check[id] = {checked : false, options : options};
	};
	
	/**
	 * Supprime un champs de la verif
	 * 
	 * @param string id
	 */
	this.remove_from_checklist = function(id){
		this.liste_check[id] = null;
		this.liste_check.clean();
	};
	
	/**
	 * Execute le check des champs programmer
	 * 
	 * @throws Alert
	 * @return boolean
	 */
	this.execute = function(){
		this.liste_check.clean();
		this.error_message = new Array();
		
		var valid_options = null;
		for (var i in this.liste_check){
			valid_options = this.liste_check[i];
			if (!valid_options.options)
				continue;
			
			if (!valid_options.checked){
				
				var name = valid_options.options.name;
				var typecheck = valid_options.options.search.type;
				var id = valid_options.options.id;
				
				switch(valid_options.options.type){
				case 'text': 
					var value = this.get_input_text(id);
					break;
				case 'file':
					var value = this.get_input_file(id);
					break;
				case 'textarea':
					var value = this.get_input_textarea(id);
					break;
				case 'tinymce':
					var value = this.get_input_tinymce(id);
					break;
				case 'select':
					var value = this.get_select(id, valid_options.options.search.compare);
					break;
				case 'multiple':
					var value = this.get_select_multiple(id);
					break;
				/*
				case 'check':
					var value = this.get_input_check(id);
					break;
				case 'radio':
					var value = this.get_input_radio(id);
					break;
				*/
				}
				
				var tab_typecheck = typecheck.split(",");
				if (tab_typecheck.lenght == 0)
					tab_typecheck[0] = typecheck;
				for (var j = 0; j<tab_typecheck.length; j++){
					switch(tab_typecheck[j]){
					case 'select':
						if (valid_options.options.type == 'multiple' || valid_options.options.type == 'select'){
							if (!value || value == '' || value == '0')
								this.add_to_error('le champs '+ name +' n\'a aucun lment slctionn.', id);
							else
								valid_options.checked = true;
						}
						break;
					case 'numeric':
						if (value != ''){
							if (isNaN(value) == true)
								this.add_to_error('le champs '+ name +' est doit tre un nombre.', id);
							else{
								if (typeof(valid_options.options.search.size) == 'object'){
									var min = valid_options.options.search.size[0];
									var max = valid_options.options.search.size[1];
									var result = this.check_size(value, min, max);
									if (result == -1)
										this.add_to_error('le champs '+ name +' est trop petit (<'+min+').', id);
									else if (result == 1)
										this.add_to_error('le champs '+ name +' est trop grand (>'+max+').', id);
									else
										valid_options.checked = true;
								}else
									valid_options.checked = true;
							}
						}
						
						break;
					case 'text':
						if (typeof(valid_options.options.search.size) == 'object'){
							var min = valid_options.options.search.size[0];
							var max = valid_options.options.search.size[1];
							var result = this.check_size(value, min, max);
							if (result == -1)
								this.add_to_error('le champs '+ name +' est trop petit (<'+min+').', id);
							else if (result == 1)
								this.add_to_error('le champs '+ name +' est trop grand (>'+max+').', id);
							else
								valid_options.checked = true;
						}
						break;
					case 'email': 
						var result = this.check_email(value);
						if (result == false)
							this.add_to_error('le champs '+ name +' n\'est pas un email.', id);
						else
							valid_options.checked = true;
						break;
					case 'passwd': 
						var value2 = this.get_input_text(valid_options.options.search.compare);
						var result = this.check_passwd(value, value2);
						if (result == false)
							this.add_to_error('votre password n\'est pas valide.', id);
						else
							valid_options.checked = true;
						break;
					case 'url': 
						var result = this.check_url(value);
						if (result == false)
							this.add_to_error('le champs '+ name +' n\'est pas une url valide.', id);
						else
							valid_options.checked = true;
						break;
					case 'regexp': 
						var regexp = valid_options.options.search.regexp;
						if (!regexp.test(value))
							this.add_to_error('le champs '+ name +' n\'est pas valide.', id);
						else
							valid_options.checked = true;
						break;
					}
				}
				
				
				valid_options.checked = false;
			}
		}
		
		return this.generate_error();
	};
	
	/**
	 * ajoute un message d'erreur
	 * 
	 * @param string message
	 */
	this.add_to_error = function( message, id ){
		this.error_message[id] = message;
	};
	
	/**
	 * Genere le message d'erreur
	 * 
	 * @return void
	 */
	this.generate_error = function(){
		var generated = '';
		for(var i in this.error_message){
			if (typeof(this.error_message[i]) == 'string')
				generated = generated + "\n" + "\t - "+this.error_message[i];
		}
		if (generated != ''){
			alert("Votre formulaire n'est pas valide :"+generated);
			return false;
		}
		return true;
	};
	
	/**
	 * Verifi si un inpout/select est check ou selectionner
	 * 
	 * @param elt
	 * @return boolean/array
	 */
	this.check_selected = function (elt){
		if (!!elt.type){ 
			if(elt.type == 'checkbox' || elt.type == 'redio'){
				if (elt.checked)
					return true;
				return false;
			}
		}else if (elt.nodeName == 'select'){
			selected = new Array();
			for (var i = 0; i < elt.options.length; i++){
				if (elt.options[ i ].selected) 
					selected.push(elt.options[ i ].value);
			}
			if (selected.length > 0)
				return true;
		}
		return false;
	};
	
	/**
	 * Verifi la taille d'un champs
	 * 
	 * @param value
	 * @param min
	 * @param max
	 * @return int (+1, -1, 0)
	 */
	this.check_size = function(value, min, max){
		var value_length = value.length;
		if (max > 0){
			if (value_length > max)
				return 1;
		}
		if (min > 0){
			if (value_length < min)
				return -1
		}
		return 0;
	};
	
	/**
	 * Verifi si c'est un email
	 * 
	 * @param value
	 * @return boolean
	 */
	this.check_email = function(value){
		var regexp = /^[a-z0-9_\-]+(\.[_a-z0-9\-]+)*@([_a-z0-9\-]+\.)+([a-z]{2}|aero|arpa|biz|com|coop|edu|gov|info|int|jobs|mil|museum|name|nato|net|org|pro|travel)$/i;
		return regexp.test(value);
	};
	
	/**
	 * Verifi coerence de passwd
	 * 
	 * @param value1
	 * @param value2
	 * @return boolean
	 */
	this.check_passwd = function(value1, value2){
		if (value1 == value2)
			return true;
		return false;
	};
	
	/**
	 * Verifi si la valeur est bien une URL
	 * 
	 * @param value
	 * @return boolean
	 */
	this.check_url = function(value){
		var regexp = /(ftp|http|https):\/\/(\w+:{0,1}\w*@)?(\S+)(:[0-9]+)?(\/|\/([\w#!:.?+=&%@!\-\/]))?/i
		return regexp.test(value);
	};
	
	/**
	 * affiche une bordure autour du champs invalider
	 * 
	 */
	this.draw_border = function(){
		
	};
	
	/**
	 * rcupere le contenu d'un champs tinyMCE
	 * 
	 * @param idelt
	 * @return string
	 */
	this.get_input_tinymce = function (idelt){
		if (!!tinyMCE && !!tinyMCE.get(idelt)){
			return tinyMCE.get(idelt).getContent();
		}
		return '';
	};
	
	/**
	 * recupere le contenu d'un champs file
	 * 
	 * @param idelt
	 * @return string
	 */
	this.get_input_file = function(idelt){
		if (!!$(idelt))
			return $(idelt).value;
		return '';
	};
	
	/**
	 * Rcupere le contenu d'un champs textearea
	 * 
	 * @param idelt
	 * @return string
	 */
	this.get_input_textarea = function(idelt){
		if (!!$(idelt))
			return $(idelt).innerHTML;
		return '';
	};
	
	/**
	 * Rcupere le contenu d'un champs input texte
	 * 
	 * @param idelt
	 * @return string
	 */
	this.get_input_text = function( idelt ){
		if (!!$(idelt))
			return $(idelt).value;
		return '';
	};
	
	/**
	 * Rcupere la valeur de la selection d'un champs select
	 * 
	 * @param idelt
	 * @param compare
	 * @return string
	 */
	this.get_select = function(idelt, compare){
		var element = $(idelt);
		if (compare == null) compare = -1;
		if (!!element && element.selectedIndex != -1 && element.selectedIndex != compare)
			return element.options[element.selectedIndex].value;
		return '';
	};
	
	/**
	 * Rcupere les valeur des champs selectionner dans un select multiple
	 * 
	 * @param idelt
	 * @return array
	 */
	this.get_select_multiple = function(idelt){
		var tab = new Array();
		var element = $(idelt);
		if (!!element){
			for (var i = 0; i < element.options.length; i++){
				if (element.options[ i ].selected) 
					tab.push(element.options[ i ].value);
			}
		}
		return tab;
	};
	
	/*
	get_input_check : function(idelt){
		
	},
	
	get_input_radio : function(idelt){
		
	}
	*/
	
};
